// src/components/Dashboard.js
import React from 'react';
import { Box, Typography, LinearProgress, List, ListItem, ListItemText, Button } from '@mui/material';

function Dashboard({ progress, onStartDisclosure }) {
  const { completed, total } = progress;

  return (
    <Box maxWidth={600} mx="auto" mt={4} p={3} boxShadow={3} borderRadius={2}>
      <Typography variant="h4" gutterBottom>
        Seller Disclosure Dashboard
      </Typography>

      <Typography>Progress: {completed} of {total} steps complete</Typography>
      <LinearProgress variant="determinate" value={(completed / total) * 100} sx={{ height: 10, mb: 2, borderRadius: 1 }} />

      <List>
        <ListItem>
          <ListItemText primary="AI-guided form completion" primaryTypographyProps={{ color: completed >= 1 ? 'success.main' : 'text.disabled' }}/>
        </ListItem>
        <ListItem>
          <ListItemText primary="Document uploads" primaryTypographyProps={{ color: completed >= 2 ? 'success.main' : 'text.disabled' }}/>
        </ListItem>
        <ListItem>
          <ListItemText primary="E-sign disclosure form" primaryTypographyProps={{ color: completed >= 3 ? 'success.main' : 'text.disabled' }}/>
        </ListItem>
      </List>

      <Button variant="contained" onClick={onStartDisclosure} sx={{ mt: 2 }}>
        Start / Continue Disclosure
      </Button>
    </Box>
  );
}

export default Dashboard;
