import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import DisclosureChat from './components/DisclosureChat';
import NavBar from './components/NavBar';

function App() {
  const [user, setUser] = useState(null);
  const [progress, setProgress] = useState({ completed: 0, total: 3 });
  const navigate = useNavigate();

  // Simulated login handler
  const handleLogin = (userData) => {
    setUser(userData);
    // After login, navigate to dashboard
    navigate('/dashboard');
  };

  const handleLogout = () => {
    setUser(null);
    navigate('/login');
  };

  // Sample function to start disclosure flow
  const startDisclosure = () => {
    navigate('/disclosure');
  };

  return (
    <>
      <NavBar user={user} onLogout={handleLogout} />
      <Routes>
        <Route
          path="/login"
          element={!user ? <Login onLogin={handleLogin} /> : <Navigate to="/dashboard" replace />}
        />
        <Route
          path="/dashboard"
          element={user ? <Dashboard progress={progress} onStartDisclosure={startDisclosure} /> : <Navigate to="/login" replace />}
        />
        <Route
          path="/disclosure"
          element={user ? <DisclosureChat sendPrompt={sendPromptToAI} /> : <Navigate to="/login" replace />}
        />
        {/* Redirect unknown paths to login or dashboard based on auth */}
        <Route path="*" element={<Navigate to={user ? "/dashboard" : "/login"} replace />} />
      </Routes>
    </>
  );
}

export default App;
