// src/App.js
import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import NavBar from './components/NavBar';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import DisclosureChat from './components/DisclosureChat';



// (Add other imports, e.g., DisclosureFlow if you have it)


function App() {
  const [user, setUser] = useState(null);
  const [progress, setProgress] = useState({ completed: 1, total: 3 });

  const handleLogin = (userData) => setUser(userData);
  const handleStartDisclosure = () => {
    alert("Starting or continuing disclosure flow...");
    // You can later extend this to route to your disclosure form or AI chat
  };


  return (

    <div>
      <NavBar /> 
    <Routes>
      <Route 
        path="/" 
        element={
          !user ? 
          <Login onLogin={handleLogin} /> : 
          <Dashboard progress={progress} onStartDisclosure={handleStartDisclosure} />
        } 
      />
      <Route path="/login" element={<Login onLogin={setUser} />} />
      <Route path="/dashboard" element={<Dashboard progress={{completed: 1, total: 3}} onStartDisclosure={() => {}} />} />

      </Routes>
      </div>
  );
}

export default App;
