// src/components/PropertyDetail.jsx
import { useEffect, useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import {
  Box, Typography, LinearProgress, List, ListItem, ListItemText, Button, Chip
} from '@mui/material';
import { getProperty } from '../api';
import DocumentPanel from './DocumentPanel';
import { buildForm2, getLatestForm2 } from '../api';


export default function PropertyDetail() {
  const { id } = useParams();

  const [prop, setProp] = useState(null);
  const [docs, setDocs] = useState([]);         // ⬅️ define docs state
  const [loading, setLoading] = useState(true);

  // ▼ new: control which "Kind" is preselected in the DocumentPanel
  const [selectedKind, setSelectedKind] = useState(null);
  const docsRef = useRef(null);

  const jumpToDocs = (kind) => {
    setSelectedKind(kind);
    // scroll after state updates
    setTimeout(() => docsRef.current?.scrollIntoView({ behavior: 'smooth', block: 'start' }), 0);
  };

  async function refresh() {
    setLoading(true);
    try {
      const data = await getProperty(id);
      setProp(data);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => { refresh(); }, [id]);

  if (loading) return <Box p={3}>Loading…</Box>;
  if (!prop) return <Box p={3}>Not found</Box>;

  const { title, address, type, progress } = prop;
  const checklist = prop.checklist ?? [];
  const completed = progress?.completed ?? 0;
  const total = progress?.total ?? 0;
  const pct = total ? (completed / total) * 100 : 0;




  return (
    <Box maxWidth={800} mx="auto" mt={4} p={3} boxShadow={3} borderRadius={2}>
      <Typography variant="h5" gutterBottom>{title}</Typography>
      <Typography variant="body1" gutterBottom>{address} • Type: {type}</Typography>

      <Typography>Progress: {progress.completed} / {progress.total}</Typography>
      <LinearProgress variant="determinate" value={pct} sx={{ height: 10, mb: 2, borderRadius: 1 }} />


{(progress?.completed ?? 0) >= (progress?.total ?? 0) && progress?.total > 0 && (
  <Box mt={2}>
    <Button
      variant="contained"
      onClick={() => alert('TODO: Generate Form 2 (PDF)')}
    >
      Generate Form 2 (PDF)
    </Button>
  </Box>
)}



      <List>
        {checklist.map(item => (
          <ListItem
            key={item.id}
            secondaryAction={!item.complete && (
              <Button size="small" onClick={() => jumpToDocs(item.id)}>
                Upload…
              </Button>
            )}
          >
            <ListItemText
              primary={item.label}
              secondary={item.required ? 'Required' : 'Optional'}
              primaryTypographyProps={{ color: item.complete ? 'success.main' : 'text.primary' }}
            />
            {item.complete ? <Chip label="Done" color="success" size="small" /> : null}
          </ListItem>
        ))}
      </List>

{/* Existing docs list (now docs is defined) */}
<List>
        {docs.map(d => (
          <ListItem
            key={d.id}
            divider
            secondaryAction={
              <Button
                size="small"
                onClick={() => window.open(`/api/documents/${d.id}/download`, '_blank')}
              >
                Download
              </Button>
            }
          >
            <ListItemText
              primary={d.filename}
              secondary={`kind: ${d.kind} • sha256: ${d.sha256.slice(0,12)}… • ${new Date(d.createdAt).toLocaleString()}`}
            />
          </ListItem>
        ))}
    {/* {docs.length === 0 && <ListItem><ListItemText primary="No documents yet." /></ListItem>}*/}
      </List>





      {/* Documents panel (preselects the kind you clicked, and refreshes progress on upload) */}
      <Box ref={docsRef} mt={3}>
        <DocumentPanel propertyId={id} onChanged={refresh} initialKind={selectedKind} />
      </Box>
    </Box>
  );
}
