// src/App.js
import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import DisclosureChat from './components/DisclosureChat';
import NavBar from './components/NavBar';
import { fetchProgress } from './api';
import ExternalLogin from './components/ExternalLogin';


function App() {
  const [user, setUser] = useState(null);
  const [progress, setProgress] = useState({ completed: 1, total: 3 });
  const navigate = useNavigate();

  const handleLogin = (userData) => {
    setUser(userData);
    navigate('/dashboard');
  };

  const handleLogout = () => {
    setUser(null);
    navigate('/login');
  };

  const startDisclosure = () => navigate('/disclosure');


  useEffect(() => {
    (async () => {
      try {
        const res = await fetch('/api/me', { credentials: 'include' });
        setUser(res.ok ? await res.json() : null);
      } catch {
        setUser(null);
      }
    })();
  }, []);

  return (
    <>
      <NavBar user={user} onLogout={handleLogout} />
      <Routes>
      <Route path="/login" element={<ExternalLogin />} />
      <Route path="/dashboard" element={user ? <Dashboard progress={progress} onStartDisclosure={startDisclosure} /> : <Navigate to="/login" replace />} />
        <Route path="/disclosure" element={user ? <DisclosureChat /> : <Navigate to="/login" replace />} />
        <Route path="*" element={<Navigate to={user ? "/dashboard" : "/login"} replace />} />
      </Routes>
    </>
  );
}

export default App;
