import { useEffect, useState } from 'react';
import { Box, Typography, List, ListItem, ListItemText, Button, TextField, MenuItem } from '@mui/material';
import { listDocuments, presignUpload, completeUpload } from '../api';

const KINDS = [
  { id: 'title_search', label: 'Title search document' },
  { id: 'pool_safety',  label: 'Pool safety certificate' },
  { id: 'smoke_alarm',  label: 'Smoke alarm compliance' },
  { id: 'bcorp_info',   label: 'Body corporate information' },
];

export default function DocumentPanel({ propertyId, onChanged }) {
  const [docs, setDocs] = useState([]);
  const [kind, setKind] = useState('title_search');
  const [file, setFile] = useState(null);
  const [busy, setBusy] = useState(false);

  async function refresh() {
    const list = await listDocuments(propertyId);
    setDocs(list);
  }
  useEffect(() => { refresh(); }, [propertyId]);

  const upload = async () => {
    if (!file) return;
    setBusy(true);
    try {
      const { uploadUrl, key } = await presignUpload(propertyId, { filename: file.name, kind });

      // PUT the file directly to S3
      const put = await fetch(uploadUrl, { method: 'PUT', body: file });
      if (!put.ok) throw new Error('upload failed');

      // tell backend to create DB row
      await completeUpload(propertyId, { key, filename: file.name, kind });

      setFile(null);
      await refresh();
      onChanged?.(); // tell parent to recompute progress
    } catch (e) {
      console.error(e);
      alert(e.message);
    } finally {
      setBusy(false);
    }
  };

  return (
    <Box mt={3}>
      <Typography variant="h6" gutterBottom>Documents</Typography>

      <Box display="flex" gap={2} alignItems="center" mb={2}>
        <TextField
          select
          label="Kind"
          size="small"
          value={kind}
          onChange={(e)=>setKind(e.target.value)}
          sx={{ minWidth: 280 }}
        >
          {KINDS.map(k => <MenuItem key={k.id} value={k.id}>{k.label}</MenuItem>)}
        </TextField>

        <input type="file" onChange={(e)=>setFile(e.target.files?.[0] ?? null)} />
        <Button disabled={!file || busy} variant="contained" onClick={upload}>Upload</Button>
      </Box>

      <List>
        {docs.map(d => (
          <ListItem key={d.id} divider>
            <ListItemText
              primary={`${d.kind} — ${d.filename}`}
              secondary={`Uploaded ${new Date(d.createdAt).toLocaleString()}`}
            />
          </ListItem>
        ))}
      </List>
    </Box>
  );
}
