import React, { useEffect, useState } from 'react';
import { Box, Button, TextField, Typography, List, ListItem, ListItemText } from '@mui/material';
import { Link } from 'react-router-dom';
import { listProperties, createProperty } from '../api';

export default function PropertyList() {
  const [items, setItems] = useState([]);
  const [form, setForm] = useState({ title: '', address: '', type: 'house', sellerEmail: '' });

  useEffect(() => { listProperties().then(setItems).catch(console.error); }, []);

  const onCreate = async () => {
    const p = await createProperty(form);
    setItems([p, ...items]);
    setForm({ title: '', address: '', type: 'house', sellerEmail: '' });

    const payload = {
        title: (title || '').trim(),
        address: (address || '').trim(),
        type: ((type || 'house').trim().toLowerCase()),
        ...(sellerEmail && sellerEmail.trim() ? { sellerEmail: sellerEmail.trim() } : {}),
      };

      // client-side validation
      if (!payload.title || !payload.address) {
        alert('Please enter both Title and Address.');
        return;
      }

      try {
        await createProperty(payload);
        setOpen(false);
        setTitle(''); setAddress(''); setType('house'); setSellerEmail('');
        await refresh();
      } catch (e) {
        // map common server messages to friendlier text
        const msg = String(e.message);
        if (msg.includes('title_address_required')) {
          alert('Please enter both Title and Address.');
        } else if (msg.includes('seller not found')) {
          alert('Seller email must belong to a user in your org.');
        } else if (msg.includes('forbidden')) {
          alert('Only Agents or Admins can create properties.');
        } else {
          alert(msg);
        }
      }

  };

  //const canCreate = (user?.roles || []).some(r => r === 'Agent' || r === 'Admin');


  return (
    <Box maxWidth={800} mx="auto" mt={4} p={3} boxShadow={3} borderRadius={2}>
      <Typography variant="h5" gutterBottom>Properties</Typography>

      <Box display="grid" gridTemplateColumns="1fr 1fr" gap={2} mb={2}>
        <TextField label="Title" value={form.title} onChange={e=>setForm(f=>({...f,title:e.target.value}))}/>
        <TextField label="Address" value={form.address} onChange={e=>setForm(f=>({...f,address:e.target.value}))}/>
        <TextField label="Type" value={form.type} onChange={e=>setForm(f=>({...f,type:e.target.value}))}/>
        <TextField label="Seller Email (optional)" value={form.sellerEmail} onChange={e=>setForm(f=>({...f,sellerEmail:e.target.value}))}/>
      </Box>

      <Button variant="contained" onClick={onCreate}>Create Property</Button>

      <List sx={{ mt:2 }}>
        {items.map(p => (
          <ListItem key={p.id} divider secondaryAction={<Link to={`/properties/${p.id}`}>Open</Link>}>
            <ListItemText
              primary={`${p.title} — ${p.address}`}
              secondary={`Type: ${p.type} • Created: ${new Date(p.createdAt).toLocaleString()}`}
            />
          </ListItem>
        ))}
      </List>
    </Box>
  );
}
