// src/App.js
import React, { useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';   // Import routing components
import Login from './components/Login';
import Dashboard from './components/Dashboard';

function App() {
  const [user, setUser] = useState(null);
  const [progress, setProgress] = useState({ completed: 1, total: 3 });

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleStartDisclosure = () => {
    alert("Starting or continuing disclosure flow...");
    // You can later extend this to route to your disclosure form or AI chat
  };

 // return (
 //   <div>
 //     {!user ? (
 //       <Login onLogin={handleLogin} />
 //     ) : (
 //       <Dashboard progress={progress} onStartDisclosure={handleStartDisclosure} />
 //     )}
 //   </div>
 // );

return (
    <Routes>
      <Route
        path="/login"
        element={<Login onLogin={handleLogin} />}
      />
      <Route
        path="/dashboard"
        element={
          user ? <Dashboard /> : <Navigate to="/login" replace />
        }
      />
      <Route
        path="/"
        element={<Navigate to={user ? "/dashboard" : "/login"} replace />}
      />
    </Routes>
  );
}

export default App;
