// src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import { Box, Typography, LinearProgress, List, ListItem, ListItemText, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { getDashboardSummary } from '../api';

export default function Dashboard() {
  const [summary, setSummary] = useState({ properties: [], totals: { completed: 0, total: 0 } });
  const navigate = useNavigate();

  useEffect(() => { getDashboardSummary().then(setSummary).catch(console.error); }, []);

  const { properties, totals } = summary;
  const overallPct = totals.total ? (totals.completed / totals.total) * 100 : 0;

  return (
    <Box maxWidth={800} mx="auto" mt={4} p={3} boxShadow={3} borderRadius={2}>
      <Typography variant="h4" gutterBottom>Seller Disclosure Dashboard</Typography>

      <Typography>Overall progress: {totals.completed} of {totals.total} steps complete</Typography>
      <LinearProgress variant="determinate" value={overallPct} sx={{ height: 10, mb: 3, borderRadius: 1 }} />

      <Typography variant="h6" gutterBottom>Properties</Typography>
      <List>
        {properties.map(p => {
          const pct = (p.progress.completed / p.progress.total) * 100;
          return (
            <ListItem key={p.id} divider
              secondaryAction={<Button onClick={() => navigate(`/properties/${p.id}`)}>Open</Button>}
            >
              <ListItemText
                primary={`${p.title} — ${p.address}`}
                secondary={`Progress: ${p.progress.completed}/${p.progress.total}`}
              />
              <Box sx={{ minWidth: 160, ml: 2 }}>
                <LinearProgress variant="determinate" value={pct} sx={{ height: 8, borderRadius: 1 }} />
              </Box>
            </ListItem>
          );
        })}
        {properties.length === 0 && (
          <ListItem><ListItemText primary="No properties yet — create one on the Properties page." /></ListItem>
        )}
      </List>
    </Box>
  );
}
