// src/api.js
export async function sendPromptToAI(prompt) {
    try {
      const response = await fetch('http://localhost:6000/api/disclosure/ai', { // backend URL
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt })
      });

      if (!response.ok) throw new Error('AI response failed');
      const data = await response.json();
      return data.reply;
    } catch (error) {
      console.error(error);
      return "Sorry, I'm having trouble responding right now.";
    }
  }

  export async function fetchProgress() {
    try {
      const res = await fetch('http://localhost:6000/api/disclosure/progress');
      if (!res.ok) throw new Error('Progress fetch failed');
      return await res.json();
    } catch (e) {
      console.error(e);
      return { completed: 0, total: 3 };
    }
  }
