// src/App.js
import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import DisclosureChat from './components/DisclosureChat';
import NavBar from './components/NavBar';
import { fetchProgress } from './api';
import ExternalLogin from './components/ExternalLogin';
import LoginAuth0 from './components/LoginAuth0';


function App() {
  const [user, setUser] = useState(null);
  const [authReady, setAuthReady] = useState(false); // NEW
  const [progress, setProgress] = useState({ completed: 1, total: 3 });
  const navigate = useNavigate();

  const handleLogin = (userData) => {
    setUser(userData);
    navigate('/dashboard');
  };

  const handleLogout = () => {
    setUser(null);
    navigate('/login');
  };

  const startDisclosure = () => navigate('/disclosure');


  useEffect(() => {
    (async () => {
      try {
        const res = await fetch('/api/me', { credentials: 'include' });
        setUser(res.ok ? await res.json() : null);
      } catch {
        setUser(null);
      }       finally {
        setAuthReady(true); // we know if logged in or not
      }
    })();
  }, []);

  if (!authReady) return null; // or a small spinner while checking

  return (
    <>
      <NavBar user={user} onLogout={handleLogout} />
      <Routes>
        {/* Public home (optional) */}
        <Route path="/" element={<div style={{padding:24}}>AppHome</div>} />

        {/* Login page with button (no auto-redirect) */}
        <Route path="/login" element={<LoginAuth0 />} />

        {/* Protected routes */}
        <Route path="/dashboard" element={user ? (
          <Dashboard progress={progress} onStartDisclosure={startDisclosure} />
        ) : <Navigate to="/login" replace />} />

        <Route path="/disclosure" element={user ? (
          <DisclosureChat />
        ) : <Navigate to="/login" replace />} />

        {/* Default redirect */}
        <Route path="*" element={<Navigate to={user ? "/dashboard" : "/"} replace />} />
      </Routes>
    </>
  );
}

export default App;
